mapper.select:
select fr.*, food.food_name from food_reserve fr,food
where r_code=#{r_code}
and fr.food_code=food.food_code



select *
from (select rownum rnum, b.*
from (select re.r_code, ro.room_name, re.reserve_date, re.start_time,
re.end_time,rv.review_code, rv.review_date, rv.content,go.file_name
from room ro, reserve re, review rv, gallery go
where ro.room_code=re.room_code and re.r_code=rv.r_code and go.room_code =re.room_code and go.gallery_num=1 and re.mem_key=#{key} order by rv.r_code desc ) b)
where rnum >= #{start} and rnum <= #{end}


select count(*)
from review where mem_key=#{key}
